const WithErrorAndHelpText = () => {
    const [value, setValue] = useState(args.value);
    const id = useId();
    const checkboxId = `checkbox-${id}`;
    const labelId = `label-${id}`;
    const helpTextId = `helptext-${id}`;

    const handleToggle = (event: FormEvent) => {
        action('onChange')(event);
        setValue((prevIsChecked) => (prevIsChecked === 'indeterminate' ? true : !prevIsChecked));
    };

    return (
        <Flex direction="column" gap={2}>
            <Flex gap={2} align="center">
                <Checkbox
                    disabled={false}
                    required={false}
                    readOnly={false}
                    onBlur={action('onBlur')}
                    onFocus={action('onFocus')}
                    id={checkboxId}
                    aria-labelledby={labelId}
                    aria-describedby={helpTextId}
                    value={value}
                    onChange={handleToggle}
                    status="error" />
                <Label id={labelId} htmlFor={checkboxId}>I agree to the terms and conditions.
                                        </Label>
            </Flex>
            <Text id={helpTextId} size="small" color="negative">Please check this box to continue.
                                </Text>
        </Flex>
    );
};

