    return (
        <Dialog.Root>
            <Dialog.Trigger>
                <Button>Open dialog</Button>
            </Dialog.Trigger>
            <Dialog.Content
                onEscapeKeyDown={() => {
                    alert('Escape key was pressed!');
                }}>
                <Dialog.Header>
                    <Dialog.Title>Dialog with onEscapeKeyDown</Dialog.Title>
                </Dialog.Header>
                <Dialog.Body>This dialog will show an alert when you press the Escape key, then close normally.
                                        </Dialog.Body>
                <Dialog.Footer>
                    <Button emphasis="default">Cancel</Button>
                    <Button>Submit</Button>
                </Dialog.Footer>
            </Dialog.Content>
