const PreventCloseOnEscape = () => {
    return (
        <Dialog.Root>
            <Dialog.Trigger>
                <Button>Open dialog</Button>
            </Dialog.Trigger>
            <Dialog.Content
                onEscapeKeyDown={(event) => {
                    event.preventDefault();
                    alert('Escape key pressed, but dialog will not close!');
                }}>
                <Dialog.Header>
                    <Dialog.Title>Dialog preventing close on Escape</Dialog.Title>
                </Dialog.Header>
                <Dialog.Body>This dialog prevents closing when Escape is pressed. You must use the Cancel button or close
                                            button to close it.
                                        </Dialog.Body>
                <Dialog.Footer>
                    <Dialog.Close>
                        <Button emphasis="default">Cancel</Button>
                    </Dialog.Close>
                    <Button>Submit</Button>
